CREATE TABLE photos ("id" INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, "title" varchar(255) DEFAULT NULL, "description" text DEFAULT NULL, "filename" varchar(255) DEFAULT NULL, "content_type" varchar(255) DEFAULT NULL, "path" varchar(255) DEFAULT NULL, "parent_id" integer DEFAULT NULL, "thumbnail" varchar(255) DEFAULT NULL, "size" integer DEFAULT NULL, "user_id" integer DEFAULT NULL, "width" integer DEFAULT NULL, "height" integer DEFAULT NULL, "created_at" datetime DEFAULT NULL, "updated_at" datetime DEFAULT NULL);
CREATE TABLE schema_info (version integer);
CREATE TABLE users ("id" INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, "login" varchar(255) DEFAULT NULL, "email" varchar(255) DEFAULT NULL, "crypted_password" varchar(40) DEFAULT NULL, "salt" varchar(40) DEFAULT NULL, "created_at" datetime DEFAULT NULL, "updated_at" datetime DEFAULT NULL, "remember_token" varchar(255) DEFAULT NULL, "remember_token_expires_at" datetime DEFAULT NULL);
